import styled from "styled-components";

export const Container = styled.div`
    width: 100%;
    height: 100px;
    display: flex;
    align-items: center;
    flex-direction: column;
`;

export const Name = styled.h1`
    width: 100%;
    text-align: center;
    font-size: 20px;
    font-weight: 700;
    color: #444;
`;

export const Username = styled.h2`
    width: 100%;
    text-align: center;
    font-size: 10px;
    font-weight: 500;
    color: #777;
    padding: 5px;
`;

export const Description = styled.p`
    width: 300px;
    height: 30px;
    margin: 10px 0;
    font-size: 12px;
    text-align: center;
    color: #777;
    line-height: 1.3;
    overflow: hidden;
    font-weight: bold;

    @media (max-width: 430px) {
        width: 250px;
    }
`;

export const DataCreate = styled.p`
    height: 2px;
    font-size: 8px;
    color: #444;
`;
