import styled from "styled-components";

export const Painel = styled.div`
    width: 600px;
    height: 400px;
    background-color: white;
    border-radius: 10px;
    border-bottom: 10px solid #8e05c2;
    margin-top: 15px;
    display: flex;
    justify-content: center;
    @media (max-width: 430px) {
        width: 300px;
    }
`;

export const Title = styled.h1`
    font-size: 20px;
    font-weight: 700;
    margin-top: 10px;
    color: black;
    width: 100%;
    text-align: center;
    height: 30px;
`;

export const ListOfFollowersContainer = styled.h1`
    height: 350px;
    width: 100%;
    padding: 10px;
    overflow-y: scroll;
`;
